import React, { useState, useEffect } from 'react';

const HoneyWebsite = () => {
  const [activePage, setActivePage] = useState('home');
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [showGame, setShowGame] = useState(false);
  const [formData, setFormData] = useState({ name: '', email: '' });
  const [formSubmitted, setFormSubmitted] = useState(false);

  // Styles
  const styles = {
    // Farbpalette
    colors: {
      honeyYellow: '#FFC145',
      honeyLight: '#FFECB3',
      cream: '#FFF8E6',
      greenLight: '#A3C9A8',
      greenDark: '#69A297',
      textDark: '#3A3A3A',
      white: '#FFFFFF',
    },

    // Globale Stile
    container: {
      fontFamily: "'Montserrat', sans-serif",
      color: '#3A3A3A',
      backgroundColor: '#FFF8E6',
      minHeight: '100vh',
      position: 'relative',
      overflowX: 'hidden',
    },

    // Navigation
    nav: {
      position: 'fixed',
      top: 0,
      width: '100%',
      backgroundColor: 'rgba(255, 248, 230, 0.95)',
      padding: '16px 5%',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      zIndex: 1000,
      boxShadow: '0 2px 15px rgba(0, 0, 0, 0.1)',
      backdropFilter: 'blur(5px)',
    },
    logo: {
      display: 'flex',
      alignItems: 'center',
      fontWeight: 700,
      fontSize: '1.5rem',
      color: '#3A3A3A',
      textDecoration: 'none',
    },
    logoImg: {
      height: '40px',
      marginRight: '10px',
      borderRadius: '50%',
      backgroundColor: '#FFC145',
    },
    navLinks: {
      display: 'flex',
      gap: '30px',
    },
    navLink: {
      textDecoration: 'none',
      color: '#3A3A3A',
      fontWeight: 500,
      position: 'relative',
      transition: 'all 0.3s ease',
      cursor: 'pointer',
    },
    activeNavLink: {
      color: '#69A297',
    },
    mobileMenu: {
      display: 'none',
      cursor: 'pointer',
      '@media (max-width: 768px)': {
        display: 'block',
      },
    },

    // Hero Section
    hero: {
      height: '100vh',
      width: '100%',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
      textAlign: 'center',
      position: 'relative',
      paddingTop: '80px',
    },
    heroBg: {
      position: 'absolute',
      top: 0,
      left: 0,
      width: '100%',
      height: '100%',
      backgroundColor: '#FFF8E6',
      backgroundImage: 'linear-gradient(rgba(255, 248, 230, 0.4), rgba(255, 248, 230, 0.9))',
      zIndex: -1,
    },
    heroContent: {
      maxWidth: '800px',
      zIndex: 1,
      padding: '0 20px',
      animation: 'fadeIn 2s ease',
    },
    heroTitle: {
      fontSize: '3.5rem',
      marginBottom: '20px',
      color: '#3A3A3A',
      '@media (max-width: 768px)': {
        fontSize: '2.5rem',
      },
    },
    heroText: {
      fontSize: '1.2rem',
      marginBottom: '40px',
      lineHeight: 1.6,
    },
    btnContainer: {
      display: 'flex',
      gap: '20px',
