import React, { useState, useEffect, useRef } from 'react';

const HoneyWebsite = () => {
  const [activePage, setActivePage] = useState('home');
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [showGame, setShowGame] = useState(false);
  const [formData, setFormData] = useState({ name: '', email: '' });
  const [formSubmitted, setFormSubmitted] = useState(false);
  const [bees, setBees] = useState([]);

  // Animation Frame für Bienen
  const animationRef = useRef(null);
  const beesRef = useRef([]);

  // Initialisiere Bienen, wenn die Home-Seite aktiv wird
  useEffect(() => {
    if (activePage === 'home') {
      // Erstelle anfängliche Bienen-Positionen
      const initialBees = [];
      for (let i = 0; i < 8; i++) {
        initialBees.push({
          id: i,
          x: Math.random() * 80 + 10, // 10-90% (x-Position)
          y: Math.random() * 80 + 10, // 10-90% (y-Position)
          size: Math.random() * 20 + 20, // 20-40px
          speedX: (Math.random() - 0.5) * 0.8, // Zufällige Richtung und Geschwindigkeit
          speedY: (Math.random() - 0.5) * 0.8,
          rotation: Math.random() * 360, // Zufällige Rotation
          rotationSpeed: (Math.random() - 0.5) * 2 // Rotationsgeschwindigkeit
        });
      }
      beesRef.current = initialBees;
      setBees(initialBees);

      // Starte Animation
      startAnimation();

      // Cleanup beim Unmount oder Seitenwechsel
      return () => {
        if (animationRef.current) {
          cancelAnimationFrame(animationRef.current);
        }
      };
    }
  }, [activePage]);

  // Animation für Bienen
  const startAnimation = () => {
    const animate = () => {
      // Aktualisiere Bienen-Positionen
      const updatedBees = beesRef.current.map(bee => {
        // Bewege Biene
        let newX = bee.x + bee.speedX;
        let newY = bee.y + bee.speedY;
        let newSpeedX = bee.speedX;
        let newSpeedY = bee.speedY;

        // Prüfe Grenzen und ändere Richtung bei Kollision
        if (newX <= 0 || newX >= 100) {
          newSpeedX = -newSpeedX;
          newX = Math.max(0, Math.min(100, newX));
        }

        if (newY <= 0 || newY >= 100) {
          newSpeedY = -newSpeedY;
          newY = Math.max(0, Math.min(100, newY));
        }

        // Aktualisiere Rotation
        let newRotation = (bee.rotation + bee.rotationSpeed) % 360;

        return {
          ...bee,
          x: newX,
          y: newY,
          speedX: newSpeedX,
          speedY: newSpeedY,
          rotation: newRotation
        };
      });

      beesRef.current = updatedBees;
      setBees([...updatedBees]);

      // Nächster Frame
      animationRef.current = requestAnimationFrame(animate);
    };

    // Starte Animations-Loop
    animationRef.current = requestAnimationFrame(animate);
  };

